int lengthOfLongestSubstring2(string s) 
{
	int len = s.size();
	int *DP = new int[len];
	DP[0] = 1;
	int max_len = 1;
	for (int i = 1; i < len; i++)
	{
		bool has_exist = false;
		int j = 0;
		set<char> mp;
		for (j = i; j >= i - DP[i-1]; j--) {
			if (mp.find(s[j])!=mp.end()) {
				has_exist = true; 
				break;
			} else {
				//std::pair<char, bool> tmpchar(s[j],true);
				mp.insert(s[j]);
			}
		}
		if (has_exist) { // 若是存在，则当前结果等于之前结果
			DP[i] = i - j;
		} else { // 若是不存在
			DP[i] = DP[i - 1] + 1;
			max_len = std::max(DP[i],max_len);
		}
	}
	cout << "dp = " << max_len << "\n";
	delete [] DP;
	return 0;
}


bool checkHasdumplate(const string &input){
    int letter[26] = {0};
    for(int i = 0; i< input.size(); i++){
        letter[input[i]-'a'] ++;
    }
    for(int i = 0; i<26; i++){
        if(letter[i]>1){
            return true;
        }
    }
    return false;
}

int lengthOfLongestSubstring(string s) {
    if(s.empty()) return 0;
    int max_len = 1;
    vector<char> res;
    auto begin = s.begin();
    string maxStr = s.substr(0,1);
    for(int i = 1; i < s.size(); i++){
        bool has_let = false;
        for (size_t j = 0; j < maxStr.size(); j++)
        {
            if(s[i] == maxStr[j]){
                has_let = true;
                if(j == 0) {
                    //maxStr = s.substr(i - max_len + 1,max_len);
                }
                break;
            }
        }
       
		string tmp = s.substr(i - max_len, max_len + 1);
		//cout << "tmp = " << tmp << endl;
        if(!checkHasdumplate(tmp)){
			maxStr = tmp;
            max_len = maxStr.size();
			//cout << "maxStr22 = " << maxStr << endl;
		}
		else {
			string tmp2 = s.substr(i - max_len, max_len);
			//cout << "tmp222 = " << tmp2 << endl;
			if (!checkHasdumplate(tmp2)) {
				maxStr = tmp;
			}
		}
    }
     //cout << "maxStr = " <<maxStr << endl;
    return max_len;
}

